// ================= HIST√ìRICO =================
var messages = $input.first().json.messages || [];
if (!Array.isArray(messages)) messages = [];

// ================= HELPERS =================
function extractText(item) {
  if (!item) return "";
  if (item.human) return String(item.human).trim();
  if (item.ai) return String(item.ai).trim();
  return "";
}

var mapExt = {
  um:1, uma:1, dois:2, duas:2, tres:3, tr√™s:3,
  quatro:4, cinco:5, seis:6, sete:7, oito:8,
  nove:9, dez:10
};

function extToNum(v) {
  if (typeof v === "number") return v;
  if (!v) return null;
  var s = String(v).toLowerCase();
  if (mapExt[s] != null) return mapExt[s];
  if (/^\d+$/.test(s)) return Number(s);
  return null;
}

// ================= REGEX =================
var dateRegex = /\b(janeiro|fevereiro|mar√ßo|marco|abril|maio|junho|julho|agosto|setembro|outubro|novembro|dezembro|jan|fev|mar|abr|mai|jun|jul|ago|set|out|nov|dez)\s+de\s+(\d{4})\b/i;
var adultosRegex = /\b(\d+|um|uma|dois|duas|tr[e√™]s|quatro|cinco|seis|sete|oito|nove|dez)\s+adultos?\b/i;
var pessoasRegex = /\b(\d+|um|uma|dois|duas|tr[e√™]s|quatro|cinco|seis|sete|oito|nove|dez)\s+pessoas?\b/i;
var criancasRegex = /\b(\d+|um|uma|dois|duas|tr[e√™]s|quatro|cinco|seis|sete|oito|nove|dez)\s+crian√ß?a?s?\b/i;

// ================= ESTADO =================
var data_viagem = null;
var qtd_adultos = 0;
var qtd_pessoas = 0;
var qtd_criancas = 0;

// ================= LOOP =================
for (var i = messages.length - 1; i >= 0; i--) {
  var text = extractText(messages[i]);
  if (!text) continue;

  // üìÖ data
  if (!data_viagem) {
    var d = text.match(dateRegex);
    if (d) data_viagem = d;
  }

  // üë• adultos expl√≠citos
  var a = text.match(adultosRegex);
  if (a) qtd_adultos = extToNum(a[1]) || qtd_adultos;

  // üë§ pessoas (assumidas como adultos depois)
  var p = text.match(pessoasRegex);
  if (p) qtd_pessoas = extToNum(p[1]) || qtd_pessoas;

  // üë∂ crian√ßas
  var c = text.match(criancasRegex);
  if (c) qtd_criancas = extToNum(c[1]) || qtd_criancas;
}

// ================= DATA PASSADA =================
function monthToNumber(mes){
  var mapa = {
    janeiro:1, fevereiro:2, mar√ßo:3, marco:3,
    abril:4, maio:5, junho:6, julho:7,
    agosto:8, setembro:9, outubro:10,
    novembro:11, dezembro:12,
    jan:1, fev:2, mar:3, abr:4, mai:5,
    jun:6, jul:7, ago:8, set:9,
    out:10, nov:11, dez:12
  };
  return mapa[mes];
}

var data_passada = false;

if (data_viagem) {
  var mes = data_viagem[1].toLowerCase();
  var ano = Number(data_viagem[2]);

  var viagem = new Date(ano, monthToNumber(mes) - 1, 1);
  var hoje = new Date();
  hoje.setDate(1);

  data_passada = viagem < hoje;
}

// ================= CONSOLIDA√á√ÉO DE ADULTOS =================
// prioridade:
// 1Ô∏è‚É£ adultos expl√≠citos
// 2Ô∏è‚É£ pessoas (se n√£o houver crian√ßas)

if (qtd_adultos <= 0 && qtd_pessoas > 0 && qtd_criancas === 0) {
  qtd_adultos = qtd_pessoas;
}

// ================= VALIDA√á√ÉO FINAL =================
var tem_adulto = true;

if (qtd_criancas > 0 && qtd_adultos <= 0) {
  tem_adulto = false;
}

// ================= RETURN =================
return [{
  json: {
    data_viagem: data_viagem
      ? `${data_viagem[1]} de ${data_viagem[2]}`
      : null,
    data_passada,
    tem_adulto
  }
}];

