// ======================================================
// INPUT
// ======================================================
const mensagemAtualRaw = $('Filter').item.json.combinedText2 || '';
const historico = $('get').first().json.messages || [];

// ======================================================
// HELPERS
// ======================================================
function normalizar(txt = '') {
  return txt
    .toLowerCase()
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, '')
    .replace(/[‚Äú‚Äù"]/g, '')
    .trim();
}

const mensagem = normalizar(mensagemAtualRaw);

// ======================================================
// LISTAS AUXILIARES
// ======================================================
const nomePasseios = (
  $('api_dev').item.json.items || []
)
  .filter(i => i.vl_adulto && Number(i.vl_adulto.replace(',', '')) > 0)
  .map(i => normalizar(i.ds_servico));

const CLASSIFICACOES_VALIDAS = [
  "clienteAnuncio",
  "clienteMonossilabico",
  "clienteAgencia",
  "clienteInfluencer",
  "clienteIdeal",
  "clientePortfolio",
  "clienteEmSantiago",
  "clientePreco",
  "clienteZerandoMundo",
  "quemSomos"
];

// ======================================================
// BUSCAR CLASSIFICA√á√ÉO ANTERIOR NO HIST√ìRICO
// ======================================================
let classificacaoAnterior = null;

for (let i = historico.length - 1; i >= 0; i--) {
  const aiMsg = historico[i]?.ai;
  if (!aiMsg) continue;

  const limpa = normalizar(aiMsg);
  if (CLASSIFICACOES_VALIDAS.includes(limpa)) {
    classificacaoAnterior = limpa;
    break;
  }
}

// ======================================================
// REGRAS DE PERSIST√äNCIA
// ======================================================
const PERSONAS_FIXAS = [
  "clienteAgencia",
  "clienteInfluencer"
];

const PERSONAS_FORTES = [
  "clienteIdeal",
  "clienteEmSantiago"
];

// ======================================================
// CLASSIFICA√á√ÉO ATUAL
// ======================================================
let classificacao = "clienteMonossilabico";

// 1Ô∏è‚É£ Cliente An√∫ncio (texto exato)
if (
  mensagem === 'ola! tenho interesse e queria mais informacoes sobre os passeios de verao!' ||
  mensagem === 'ola! tenho interesse e queria mais informacoes sobre as ofertas de black friday!'
) {
  classificacao = "clienteAnuncio";
}

// 2Ô∏è‚É£ Quem somos
else if (mensagem.includes('quem somos')) {
  classificacao = "quemSomos";
}

// 3Ô∏è‚É£ Influencer (contexto de m√≠dia)
else if (
  /permuta|sou influenciador|influencer/.test(mensagem) ||
  (
    mensagem.includes('parceria') &&
    /divulgacao|publicidade|midia|instagram|tiktok|youtube|alcance|seguidores/.test(mensagem)
  )
) {
  classificacao = "clienteInfluencer";
}

// 4Ô∏è‚É£ Ag√™ncia (contexto comercial)
else if (
  /agencia|operadora|revenda|terceiros/.test(mensagem) ||
  (
    mensagem.includes('parceria') &&
    /clientes|vendas|revenda|operar|grupos|comercial/.test(mensagem)
  )
) {
  classificacao = "clienteAgencia";
}

// 5Ô∏è‚É£ Zerando o Mundo (pacote completo)
else if (
  /pacote completo|pacote de viagem|pacote de viagens|voo|passagem|passagens aereas|hotel|embarque|aeroporto/.test(mensagem)
) {
  classificacao = "clienteZerandoMundo";
}

// 6Ô∏è‚É£ Cliente em Santiago (urgente)
else if (
  /estou em santiago|estou no chile|estou no atacama|hoje|amanha|agora/.test(mensagem) &&
  /passeio|transfer|preciso|quero/.test(mensagem)
) {
  classificacao = "clienteEmSantiago";
}

// 7Ô∏è‚É£ Cliente Pre√ßo
else if (
  nomePasseios.some(p => mensagem.includes(p)) &&
  /valor|preco|quanto custa|quero saber|informacao|informacoes/.test(mensagem)
) {
  classificacao = "clientePreco";
}

// 8Ô∏è‚É£ Portf√≥lio
else if (mensagem.includes('portfolio')) {
  classificacao = "clientePortfolio";
}

// 9Ô∏è‚É£ Cliente Ideal (mensagem completa)
else {
  const temNome =
    /(ola|oi|bom dia|boa tarde|boa noite)\s+(sou|me chamo|meu nome)/.test(mensagem) ||
    /\b(sou|me chamo|meu nome)\s+[a-z]{2,}/.test(mensagem);

  const regexMes =
    /(janeiro|fevereiro|marco|abril|maio|junho|julho|agosto|setembro|outubro|novembro|dezembro|jan|fev|mar|abr|mai|jun|jul|ago|set|out|nov|dez)/;

  const regexDataNumerica =
    /\d{1,2}\/\d{1,2}(\/\d{2,4})?/;

  const regexAno =
    /\b20\d{2}\b/;

  const temData =
    regexMes.test(mensagem) ||
    regexDataNumerica.test(mensagem) ||
    regexAno.test(mensagem);

  const temQtd =
    /pessoas|adultos|criancas|filhos|filhas|filho|filha|esposa|marido/.test(mensagem) ||
    /\b\d+\s+(pessoas|adultos|criancas|filhos|filhas)\b/.test(mensagem);

  if (temNome && temData && temQtd) {
    classificacao = "clienteIdeal";
  }
}

// ======================================================
// AJUSTE FINAL COM HIST√ìRICO
// ======================================================
if (classificacaoAnterior) {

  // Persona fixa nunca muda
  if (PERSONAS_FIXAS.includes(classificacaoAnterior)) {
    classificacao = classificacaoAnterior;
  }

  // Evita downgrade forte
  else if (
    PERSONAS_FORTES.includes(classificacaoAnterior) &&
    classificacao === "clienteMonossilabico"
  ) {
    classificacao = classificacaoAnterior;
  }
}

// ======================================================
// RETORNO (FORMATO CORRETO N8N)
// ======================================================
return [
  {
    json: {
      classificacao,
      classificacaoAnterior,
      mensagemOriginal: mensagemAtualRaw
    }
  }
];
