return items.map(item => {
  // Número do processo
  let rawProcesso = item.json['Números dos Processos'] || '';
  if (typeof rawProcesso !== 'string') rawProcesso = String(rawProcesso);

  // Remove tudo que não for número
  let numero = rawProcesso.replace(/[^\d]/g, '');

  // Classe e Órgão
  const classeRaw = item.json['Classe '] || '';
  const orgaoRaw = item.json['Orgao'] || '';

  // Validação: número de processo válido (20 dígitos) ou ambos classe e órgão preenchidos
  const valido = (numero.length === 20) || (classeRaw && orgaoRaw);

  return {
    json: {
      ...item.json,
      numeroProcesso: numero, // já formatado
      valido: !!valido         // true ou false
    }
  };
});

const apiKey = 'cDZHYzlZa0JadVREZDJCendQbXY6SkJlTzNjLV9TRENyQk1RdnFKZGRQdw==';

return items.map(item => {
  let numero = item.json.numeroProcesso || '';
  const classe = item.json['Classe '] || '';
  const orgao = item.json.Orgao || '';
  const size = item.json.size || 10;         // quantidade por página
  const searchAfter = item.json.searchAfter || null; // ponteiro da última página

  // Define tipo de consulta automaticamente
  let tipoConsulta = '';
  if (numero) {
    tipoConsulta = 'numero';
  } else if (classe && orgao) {
    tipoConsulta = 'classe';
  } else {
    // Se não tem numero nem classe+orgao, assumimos paginação
    tipoConsulta = 'paginacao';
  }

  // Define URL com base no tipo
  let url = 'https://api-publica.datajud.cnj.jus.br/api_publica_trf1/_search';
  if (tipoConsulta === 'classe') {
    url = 'https://api-publica.datajud.cnj.jus.br/api_publica_tjdft/_search';
  }

  // Monta o corpo da requisição
  let body = {};
  if (tipoConsulta === 'numero') {
    body = { query: { match: { numeroProcesso: numero } } };
  } else if (tipoConsulta === 'classe') {
    body = {
      query: {
        bool: {
          must: [
            { match: { "classe.codigo": classe } },
            { match: { "orgaoJulgador.codigo": orgao } }
          ]
        }
      }
    };
  } else if (tipoConsulta === 'paginacao') {
    body = {
      size: size,
      sort: [{ "@timestamp": { order: "asc" } }],
      query: { match_all: {} }
    };
    if (searchAfter) body.search_after = searchAfter;
  }

  // Determina se é válido
  const valido = !!(numero || (classe && orgao) || tipoConsulta === 'paginacao');

  return {
    json: {
      ...item.json,
      numeroProcesso: numero,
      valido,
      apiKey,
      url,
      body
    }
  };
});

