  const searchAfter = item.json.searchAfter || null; // ponteiro da última página

  // URL da API TRF1
  const url = 'https://api-publica.datajud.cnj.jus.br/api_publica_trf1/_search';

  // Corpo da requisição para paginação geral
  const body = {
    size: size,
    sort: [{ "@timestamp": { order: "asc" } }],
    query: { match_all: {} }
  };

  if (searchAfter) {
    body.search_after = searchAfter;
  }

  return {
    json: {
      ...item.json,
      valido: true,
      apiKey,
      url,
      body
    }
  };
});

return items.flatMap(item => {
  const data = Array.isArray(item.json) ? item.json[0] : item.json;
  const hits = data?.hits?.hits || [];

  return hits.map(proc => {
    const src = proc._source || {};

    const numero = src.numeroProcesso || "N/A";
    const classe = src.classe?.nome || "N/A";
    const tribunal = src.tribunal || "N/A";
    const grau = src.grau || "N/A";
    const orgao = src.orgaoJulgador?.nome || "N/A";
    const assunto = (src.assuntos && src.assuntos.length > 0) 
      ? src.assuntos.map(a => a.nome).join(", ") 
      : "N/A";
    const ajuizamento = src.dataAjuizamento 
      ? new Date(src.dataAjuizamento).toLocaleDateString("pt-BR") 
      : "N/A";

    const movimentos = (src.movimentos || []).map(m => {
      const data = m.dataHora 
        ? new Date(m.dataHora).toLocaleDateString("pt-BR") 
        : "N/A";
      const nome = m.nome || "N/A";

      let complemento = "";
      if (m.complementosTabelados && m.complementosTabelados.length > 0) {
        complemento = " (" + m.complementosTabelados
          .map(c => `${c.nome}: ${c.valor}`)
          .join(", ") + ")";
      }

      return `${data} - ${nome}${complemento}`;
    });

    // Cada processo será um item do n8n
    return {
      json: {
        numeroProcesso: numero,
        classe,
        tribunal,
        grau,
        orgaoJulgador: orgao,
        assunto,
        dataAjuizamento: ajuizamento,
        movimentos
      }
    };
  });
});

